import React, { useState } from "react";
import { Box, FormControl, styled, InputBase, Button, TextareaAutosize } from "@mui/material";
import { DatePicker } from "@mui/lab";

const Container = styled(Box)`
  margin: 50px 100px;
`;

const StyledControl = styled(FormControl)`
  margin-top: 10px;
  display: flex;
  flex-direction: column;
  width: 100%;
  justify-content: space-between;
`;

const InputTextField = styled(InputBase)`
  flex: 1;
  margin: 0 30px;
  font-size: 25px;
`;

const PublishButton = styled(Button)`
  width: auto;
  height: auto;
  padding: 8px 16px;
  display: flex;
  justify-content: flex-end;
`;

const initialPost = {
  title: '',
  description: '',
  username: '',
  gender: '',
  years: '',
  months: '',
  country: '',
  state: '',
  createdDate: new Date()
}

const CreatePost = () => {
  const [post, setPost] = useState(initialPost);

  const handleDescriptionChange = (e) => {
    let newValue = e.target.value;

    newValue = newValue.replace(/\bsex\b/gi, 's*x');
    newValue = newValue.replace(/\bbetichod\b/gi, 'beti***d');

    newValue = newValue.replace(/\d/g, '');

    setPost({ ...post, description: newValue });
  };

  return (
    <Container>
      <StyledControl>
        <InputTextField
          placeholder="Name"
          value={post.username}
          onChange={(e) => setPost({ ...post, username: e.target.value })}
        />
        <InputTextField
          placeholder="Gender"
          value={post.gender}
          onChange={(e) => setPost({ ...post, gender: e.target.value })}
        />
        <InputTextField
          placeholder="Country"
          value={post.country}
          onChange={(e) => setPost({ ...post, country: e.target.value })}
        />
        <InputTextField
          placeholder="State"
          value={post.state}
          onChange={(e) => setPost({ ...post, state: e.target.value })}
        />
        <DatePicker
          label="Date of Birth"
          value={post.createdDate}
          onChange={(newValue) => setPost({ ...post, createdDate: newValue })}
          renderInput={(params) => <InputTextField {...params} />}
        />
        <InputTextField
          placeholder="Years"
          value={post.years}
          onChange={(e) => setPost({ ...post, years: e.target.value })}
        />
        <InputTextField
          placeholder="Months"
          value={post.months}
          onChange={(e) => setPost({ ...post, months: e.target.value })}
        />
        <InputTextField
          placeholder="Title"
          value={post.title}
          onChange={(e) => setPost({...post, title: e.target.value})}
          inputProps={{ maxLength: 300 }}
       />
        <PublishButton variant="contained">
          Publish
        </PublishButton>
      </StyledControl>
      <Textarea
        minRows={5}
        placeholder="Tell your story..."
        value={post.description}
        onChange={handleDescriptionChange}
      />
    </Container>
  );
};

export default CreatePost;
