import React, { useState } from "react";
import { Box, FormControl, styled, InputBase, Button, TextareaAutosize } from "@mui/material";

const Container = styled(Box)`
  margin: 50px 100px;
`;

const StyledControl = styled(FormControl)`
  margin-top: 10px;
  display: flex;
  flex-direction: row;
`;

const InputTextField = styled(InputBase)`
  flex: 1;
  margin: 0 30px;
  font-size: 25px;
`;

const Textarea = styled(TextareaAutosize)`
    width: 100%;
    margin-top: 50px;
    font-size: 18px;
    border: none;
    &:focus-visible {
        outline: none;
    }
`;

const initialPost = {
    title: '',
    description: '',
    username: '',
    createdDate: new Date()
}

const CreatePost = () => {
    const [post, setPost] = useState(initialPost);

    // Function to validate mobile number input
    const validateMobileNumber = (value) => {
        const regex = /^\d*$/; // Regex to allow only digits
        return regex.test(value);
    };

    // Handler for description change
    const handleDescriptionChange = (e) => {
        const newValue = e.target.value;
        if (validateMobileNumber(newValue)) {
            // If input is a valid mobile number, don't update the state
            return;
        }
        // Otherwise, update the state
        setPost({...post, description: newValue});
    };

    return (
        <Container>
            <StyledControl>
                <InputTextField
                    placeholder="Title"
                    value={post.title}
                    onChange={(e) => setPost({...post, title: e.target.value})}
                />
                <Button variant="contained">
                    Publish
                </Button>
            </StyledControl>
            <Textarea
                minRows={5}
                placeholder="Tell your story..."
                value={post.description}
                onChange={handleDescriptionChange}
            />
        </Container>
    );
};

export default CreatePost;
