import { useState } from 'react';
import { Box, TextField, Button, styled } from '@mui/material';

const Component = styled(Box)`
  width: 500px;
  margin: auto;
  box-shadow: 5px 2px 5px 2px rgb(0 0 0 / 0.6);
  background: linear-gradient(to right, black 50%, white 50%);
`;

const Image = styled('img')({
  width: 100,
  display: 'flex',
  margin: 'auto',
  padding: '50px 0 0',
});

const Wrapper = styled(Box)`
  padding: 30px 30px;
  display: flex;
  flex-direction: column;
  align-items: center;
  & > * {
    margin-bottom: 10px;
  }
`;

const SignUpButton = styled(Button)(({ theme, isClicked }) => ({
  background: isClicked ? 'black' : 'white',
  color: isClicked ? 'white' : 'black',
  transition: 'background-color 0.3s, color 0.3s',
  boxShadow: isClicked ? '0px 0px 10px 9px rgba(0, 0, 0, 0.9)' : 'none', // Set box shadow to none when not clicked
  height: '40px',
  width: '100px',
  margin: '19px 30px',
  '&:hover': {
    backgroundColor: isClicked ? 'black' : 'white',
    color: isClicked ? 'white' : 'black',
    boxShadow: isClicked ? '0px 0px 10px 9px rgba(0, 0, 0, 0.9)' : 'none', // Add box shadow on hover only when clicked
  },
  [theme.breakpoints.down('sm')]: {},
}));

const LoginButton = styled(Button)(({ theme, isClicked }) => ({
  background: isClicked ? 'white' : 'black',
  color: isClicked ? 'black' : 'white',
  boxShadow: isClicked ? '0px 0px 10px 9px rgba(255, 255, 255, 0.9)' : '0px 0px 10px 9px rgba(0, 0, 0, 0.9)',
  transition: 'background-color 0.3s, color 0.3s',
  height: '40px',
  width: '100px',
  margin: '29px 35px',
  '&:hover': {
    backgroundColor: 'white',
    color: 'black',
  },
  [theme.breakpoints.down('sm')]: {},
}));

const Login = () => {
  const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png';

  const [isSignUpClicked, setSignUpClicked] = useState(false);
  const [isLoginClicked, setLoginClicked] = useState(true); // Login button should be shown by default

  const toggleSignup = () => {
    setSignUpClicked(true);
    setLoginClicked(false);
  };

  const toggleLogin = () => {
    setSignUpClicked(false);
    setLoginClicked(true);
  };

  return (
    <Component>
      <Box>
        <Image src={imageURL} alt="login" />
        <Wrapper>
          <div>
            {isLoginClicked ? (
              <>
                <LoginButton variant="contained" onClick={toggleLogin} isClicked={isLoginClicked}>
                  Log in
                </LoginButton>
                <SignUpButton onClick={toggleSignup} variant="contained" isClicked={isSignUpClicked}>
                  Sign up
                </SignUpButton>
              </>
            ) : (
              <>
                <SignUpButton onClick={toggleSignup} variant="contained" isClicked={isSignUpClicked}>
                  Sign up
                </SignUpButton>
                <LoginButton variant="contained" onClick={toggleLogin} isClicked={isLoginClicked}>
                  Log in
                </LoginButton>
              </>
            )}
          </div>
          {isLoginClicked ? (
            <>
              <TextField
                variant="standard"
                label="Enter Username"
                placeholder="User Name" // Placeholder text for the username
                InputLabelProps={{
                  style: { color: 'white' }, // Color of the label when not focused
                }}
                InputProps={{
                  style: { color: 'white' }, // Color of the input text when not focused
                }}
                sx={{ backgroundColor: 'gray', marginBottom: '10px' }}
              />
              <TextField variant="standard" label="Enter Password" sx={{ backgroundColor: 'gray', marginBottom: '10px' }} />
              <Button
                variant="contained"
                style={{
                  width: '50%',
                  background: 'linear-gradient(to right, white 50%, black 50%)',
                  color: 'black',
                  marginBottom: '10px',
                }}
              >
                Login
              </Button>
            </>
          ) : (
            <>
              <TextField variant="standard" label="Name" placeholder="Enter Name" sx={{ backgroundColor: 'gray', marginBottom: '10px' }} />
              <TextField variant="standard" label="Enter Username" sx={{ backgroundColor: 'gray', marginBottom: '10px' }} />
              <TextField variant="standard" label="Enter Password" sx={{ backgroundColor: 'gray', marginBottom: '10px' }} />
              <TextField variant="standard" label="Gender" sx={{ backgroundColor: 'gray', marginBottom: '10px' }} />
              <TextField variant="standard" label="Age" sx={{ backgroundColor: 'gray', marginBottom: '10px' }} />
              <TextField variant="standard" label="Mobile" sx={{ backgroundColor: 'gray', marginBottom: '10px' }} />
              <Button
                variant="contained"
                style={{
                  width: '50%',
                  background: 'linear-gradient(to right, white 50%, black 50%)',
                  color: 'black',
                  marginBottom: '10px',
                }}
              >
                Signup
              </Button>
            </>
          )}
        </Wrapper>
      </Box>
    </Component>
  );
};

export default Login;
