// Handler for description change
const handleDescriptionChange = (e) => {
  let newValue = e.target.value;

  // Replace occurrences of "sex" with "s*x"
  newValue = newValue.replace(/\bsex\b/gi, 's*x');

  // Check if input contains a phone number
  if (/\d{10}/.test(newValue)) {
      // If input contains a phone number, don't update the state
      return;
  }

  // Update the state
  setPost({ ...post, description: newValue });
};
