const Login = () => {
    const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png';
  
    const [account, toggleAccount] = useState('login');
    const [loginButtonColor, setLoginButtonColor] = useState('blue'); // State to track button color
  
    const toggleSignup = () => {
      toggleAccount(account === 'login' ? 'signup' : 'login');
    };
  
    const handleLoginClick = () => {
      // Change color of the "Log in" button
      setLoginButtonColor('black');
      // Add additional functionality as needed
    };
  
    return (
      <Component>
        <Box>
          <Image src={imageURL} alt="login" />
          <Wrapper>
            <div>
              {account === 'login' ? (
                <>
                  <LoginButton variant="contained">Log in</LoginButton>
                  <SignUpButton onClick={() => toggleSignup()} variant="contained">Sign up</SignUpButton>
                </>
              ) : (
                <>
                  <SignUpButton onClick={() => toggleSignup()} variant="contained">Signup</SignUpButton>
                  <LoginButton variant="contained">Log in</LoginButton>
                </>
              )}
            </div>
            {account === 'login' ? (
              <>
                <TextField variant="standard" label="Enter Username" />
                <TextField variant="standard" label="Enter Password" />
                <Button variant="contained" onClick={handleLoginClick} style={{ backgroundColor: loginButtonColor }}>Log in</Button>
              </>
            ) : (
              <>
                <TextField variant="standard" label="Enter Name" />
                <TextField variant="standard" label="Enter Username" />
                <TextField variant="standard" label="Enter Password" />
                <Button variant="contained">Sign up</Button>
              </>
            )}
          </Wrapper>
        </Box>
      </Component>
    );
  };
  
  export default Login;
