import React, { useState } from "react";
import { DatePicker } from "@mui/lab"; // Importing DatePicker from @mui/lab
import { TextareaAutosize as Textarea } from "@mui/material";
import { Box, FormControl, styled, InputBase, Button } from "@mui/material"; // Removed DatePicker from here

const Container = styled(Box)`
  margin: 50px 100px;
`;

const StyledControl = styled(FormControl)`
  margin-top: 10px;
  display: flex;
  flex-direction: column;
  width: 100%;
  justify-content: space-between;
`;

const InputTextField = styled(InputBase)`
  flex: 1;
  margin: 0 30px;
  font-size: 25px;
`;

const PublishButton = styled(Button)`
  width: auto;
  height: auto;
  padding: 8px 16px;
  display: flex;
  justify-content: flex-end;
`;

const initialPost = {
  title: '',
  description: '',
  username: '',
  gender: '',
  years: '',
  months: '',
  country: '',
  state: '',
  startDate: null,
  endDate: null,
};

const CreatePost = () => {
  const [post, setPost] = useState(initialPost);

  const handleDescriptionChange = (e) => {
    let newValue = e.target.value;
    // Your existing description change logic
    setPost({ ...post, description: newValue });
  };

  const calculateDuration = () => {
    if (post.startDate && post.endDate) {
      const diffInMs = Math.abs(post.endDate - post.startDate);
      const years = Math.floor(diffInMs / (1000 * 60 * 60 * 24 * 365));
      const months = Math.floor((diffInMs % (1000 * 60 * 60 * 24 * 365)) / (1000 * 60 * 60 * 24 * 30));
      return `${years} years ${months} months`;
    }
    return '';
  };

  return (
    <Container>
      <StyledControl>
        <InputTextField
          placeholder="Name"
          value={post.username}
          onChange={(e) => setPost({ ...post, username: e.target.value })}
        />
        <InputTextField
          placeholder="Gender"
          value={post.gender}
          onChange={(e) => setPost({ ...post, gender: e.target.value })}
        />
        <InputTextField
          placeholder="Country"
          value={post.country}
          onChange={(e) => setPost({ ...post, country: e.target.value })}
        />
        <InputTextField
          placeholder="State"
          value={post.state}
          onChange={(e) => setPost({ ...post, state: e.target.value })}
        />
        <DatePicker
          label="Start Date"
          value={post.startDate}
          onChange={(newValue) => setPost({ ...post, startDate: newValue })}
          renderInput={(params) => <InputTextField {...params} />}
        />
        <DatePicker
          label="End Date"
          value={post.endDate}
          onChange={(newValue) => setPost({ ...post, endDate: newValue })}
          renderInput={(params) => <InputTextField {...params} />}
        />
        <InputTextField
          placeholder="Title"
          value={post.title}
          onChange={(e) => setPost({ ...post, title: e.target.value })}
          inputProps={{ maxLength: 300 }}
        />
        <PublishButton variant="contained">
          Publish
        </PublishButton>
      </StyledControl>
      <Textarea
        minRows={5}
        placeholder="Tell your story..."
        value={post.description}
        onChange={handleDescriptionChange}
      />
      <div>{calculateDuration()}</div>
    </Container>
  );
};

export default CreatePost;
