import { useState } from 'react';
import { Box, TextField, Button, styled } from '@mui/material';

const Component = styled(Box)`
  width: 400px;
  margin: auto;
  box-shadow: 5px 2px 5px 2px rgb(0 0 0 / 0.6);
  background: linear-gradient(to right, black 50%, white 50%);
`;

const Image = styled('img')({
  width: 100,
  display: 'flex',
  margin: 'auto',
  padding: '50px 0 0',
});

const Wrapper = styled(Box)`
  padding: 20px 35px;
  display: flex;
  flex-direction: column;
  align-items: center;
  & > * {
    margin-bottom: 10px;
  }
`;

const StyledButton = styled(Button)(({ theme }) => ({
  borderRadius: '2px', 
  color: 'black',
  transition: 'background-color 0.3s, color 0.3s',
  height: '40px', 
  width: '100px',
  margin: '0 25px',
  [theme.breakpoints.down('sm')]: {
    // Add mobile styles if needed
  },
}));

const LoginButton = styled(StyledButton)(({ theme }) => ({
  background: 'linear-gradient(to right, black 50%, white 50%)',
  boxShadow: '0px 0px 10px 9px rgba(255, 255, 255, 0.9)',
  '&:hover': {
    backgroundColor: 'black',
    color: 'white',
  },
}));

const SignUpButton = styled(StyledButton)(({ theme }) => ({
  background: 'linear-gradient(to right, white 50%, black 50%)',
  boxShadow: '0px 0px 10px 9px rgba(0, 0, 0, 0.9)', 
  '&:hover': {
    backgroundColor: 'white',
    color: 'black',
  },
}));

const Login = () => {
  const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png';

  const [account, toggleAccount] = useState('login');

  const toggleSignup = () => {
    toggleAccount('signup');
  };

  const toggleLogin = () => {
    toggleAccount('login');
  };

  return (
    <Component>
      <Box>
        <Image src={imageURL} alt="login" />
        <Wrapper>
          <div>
            {account === 'login' ? (
              <>
                <LoginButton variant="contained" onClick={toggleLogin}>Log in</LoginButton>
                <SignUpButton onClick={toggleSignup} variant="contained">Sign up</SignUpButton>
              </>
            ) : (
              <>
                <SignUpButton onClick={toggleSignup} variant="contained">Sign up</SignUpButton>
                <LoginButton variant="contained" onClick={toggleLogin}>Log in</LoginButton>
              </>
            )}
          </div>
          {account === 'login' ? (
            <>
              <TextField variant="standard" label="Enter Username" sx={{ backgroundColor: 'gray' }} />
              <TextField variant="standard" label="Enter Password" sx={{ backgroundColor: 'gray' }} />
              <Button variant="contained">Login</Button>
            </>
          ) : (
            <>
              <TextField variant="standard" label="Enter Name" sx={{ backgroundColor: 'gray' }} />
              <TextField variant="standard" label="Enter Username" sx={{ backgroundColor: 'gray' }} />
              <TextField variant="standard" label="Enter Password" sx={{ backgroundColor: 'gray' }} />
              <Button variant="contained">Signup</Button>
            </>
          )}
        </Wrapper>
      </Box>
    </Component>
  );
};

export default Login;
