import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, Box, Button } from '@mui/material';

const Header = () => {
  const [currentLine, setCurrentLine] = useState(0);
  const lines = [
    "Love and Loss",
    "Life is a magnificent journey filled with unpredictable twists and turns",
    // Add your other lines here
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentLine((prevLine) => (prevLine + 1) % lines.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const maxLength = Math.max(...lines.map(line => line.length)); // Find the length of the longest line

  return (
    <AppBar position="static" sx={{ backgroundColor: 'black' }}>
      <Toolbar sx={{ display: 'flex', justifyContent: 'space-between', width: '100%', marginLeft: '250px' }}>
        <Box sx={{ }}>
          <Typography variant="h6" component="div">
            LIFE IS 
          </Typography>
          <Typography variant="body1" sx={{ wordWrap: 'break-word', backgroundColor: 'white', padding: '20px', marginTop: '20px', marginBottom: '20px', marginRight: `${maxLength * 8}px`, color: 'black' }}>
            {lines[currentLine]}
          </Typography>
          <Box sx={{ position: 'fixed', right: '50px', bottom: '50px', display: 'flex', flexDirection: 'column', alignItems: 'flex-end' }}>
            <Button variant="contained" style={{ color: 'white', backgroundColor: 'green', marginBottom: '20px' }}>Login</Button>
            <Button variant="contained" style={{ color: 'white', backgroundColor: 'green', marginBottom: '20px', marginLeft: '10px' }}>Signup</Button>
          </Box>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
