import React from 'react'; // Import React
import { Button, Table, TableBody, TableCell, TableRow, TableHead, styled } from '@mui/material'; // Import necessary components from Material-UI

import { categories } from '../../constants/data'; // Import categories data

// StyledTable component to style the Table
const StyledTable = styled(Table)`
    border: 10px solid rgba(224, 224, 224, 1); // Styling border
`;

// StyledButton component to style the Button
const StyledButton = styled(Button)`
    margin: 20px; // Adding margin
    width: 85%; // Setting width
    background: #6495ED; // Setting background color
    color: #fff; // Setting text color
`;

// CenteredTableCell component to style the TableCell
const CenteredTableCell = styled(TableCell)`
    text-align: center; // Aligning text to center
`;

// Categories component
const Categories = () => {
    return (
        <>
            {/* Rendering the styled table */}
            <StyledTable>
                {/* Table head */}
                <TableHead>
                    <TableRow>
                        <CenteredTableCell>
                            Filter
                        </CenteredTableCell>
                    </TableRow>
                </TableHead>
                {/* Table body */}
                <TableBody>
                    {/* Mapping through categories and rendering rows */}
                    {categories.map(category => (
                        <TableRow key={category.id}>
                            <TableCell>
                                {category.type}
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </StyledTable>
            {/* Rendering the styled button */}
            <StyledButton>create</StyledButton>
        </>
    );
};

export default Categories; // Exporting the Categories component
