import React, { useState } from "react";
import { Box, FormControl, styled, InputBase, Button, TextareaAutosize } from "@mui/material";

const Container = styled(Box)`
  margin: 50px 100px;
`;

const StyledControl = styled(FormControl)`
  margin-top: 10px;
  display: flex;
  flex-direction: row;
`;

const InputTextField = styled(InputBase)`
  flex: 1;
  margin: 0 30px;
  font-size: 25px;
`;

const Textarea = styled (TextareaAutosize)`
    width: 100%;
    margin top: 50px;
    font-size: 18px;
    border: none;
    &:focus-visible {
        outline: none;
    }
`;

const initialPost ={
    title: '',
    description: '',
    username: '',
    createdDate: new Date()
}

const CreatePost = () => {
    const [post, setPost] = useState(initialPost);

    const handleTitleChange = (e) => {
        const value = e.target.value.substring(0, 8); // Limit to 8 characters
        setPost({...post, title: value});
    }

    const handleDescriptionChange = (e) => {
        const value = e.target.value.substring(0, 6); // Limit to 6 characters
        setPost({...post, description: value});
    }

    return (
        <Container>
            <StyledControl>
                <InputTextField
                    placeholder="title"
                    value={post.title}
                    onChange={handleTitleChange}
                />
                <Button variant="contained">
                    publish
                </Button>
            </StyledControl>
            <Textarea
                minRows={5}
                placeholder="tell me story............."
                value={post.description}
                onChange={handleDescriptionChange}
            />
        </Container>
    );
};

export default CreatePost;
