import { useState } from 'react';
import { Box, TextField, Button, styled } from '@mui/material';

const Component = styled(Box)`
  width: 500px;
  margin: auto;
  box-shadow: 5px 2px 5px 2px rgb(0 0 0 / 0.6);
  background: linear-gradient(to right, black 50%, white 50%);
`;

const Image = styled('img')({
  width: 100,
  display: 'flex',
  margin: 'auto',
  padding: '50px 0 0',
});

const Wrapper = styled(Box)`
  padding: 30px 30px;
  display: flex;
  flex-direction: column;
  align-items: center;
  & > * {
    margin-bottom: 10px;
  }
`;

const SignUpButton = styled(Button)(({ theme, isClicked }) => ({
  background: isClicked ? 'black' : 'white',
  color: isClicked ? 'white' : 'black',
  transition: 'background-color 0.3s, color 0.3s',
  boxShadow: isClicked ? '0px 0px 10px 9px rgba(0, 0, 0, 0.9)' : 'none', // Set box shadow to none when not clicked
  height: '40px',
  width: '100px',
  margin: '19px 30px',
  '&:hover': {
    backgroundColor: isClicked ? 'black' : 'white',
    color: isClicked ? 'white' : 'black',
    boxShadow: isClicked ? '0px 0px 10px 9px rgba(0, 0, 0, 0.9)' : 'none', // Add box shadow on hover only when clicked
  },
  [theme.breakpoints.down('sm')]: {},
}));

const LoginButton = styled(Button)(({ theme, isClicked }) => ({
  background: isClicked ? 'white' : 'black',
  color: isClicked ? 'black' : 'white',
  boxShadow: isClicked ? '0px 0px 10px 9px rgba(255, 255, 255, 0.9)' : '0px 0px 10px 9px rgba(0, 0, 0, 0.9)',
  transition: 'background-color 0.3s, color 0.3s',
  height: '40px',
  width: '100px',
  margin: '29px 35px',
  '&:hover': {
    backgroundColor: 'white',
    color: 'black',
  },
  [theme.breakpoints.down('sm')]: {},
}));

const BoldGreenText = styled('span')({
  color: 'green',
  fontWeight: 'bold',
});

const GreenLabel = styled('span')({
  color: 'green',
  fontWeight: 'bold',
  marginBottom: '5px',
});

const Login = () => {
  const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png';

  const [isSignUpClicked, setSignUpClicked] = useState(false);
  const [isLoginClicked, setLoginClicked] = useState(true); // Login button should be shown by default

  const toggleSignup = () => {
    setSignUpClicked(true);
    setLoginClicked(false);
  };

  const toggleLogin = () => {
    setSignUpClicked(false);
    setLoginClicked(true);
  };

  return (
    <Component>
      <Box>
        <Image src={imageURL} alt="login" />
        <Wrapper>
          <div>
            {isLoginClicked ? (
              <>
                <LoginButton variant="contained" onClick={toggleLogin} isClicked={isLoginClicked}>
                  <BoldGreenText>Log in</BoldGreenText>
                </LoginButton>
                <SignUpButton onClick={toggleSignup} variant="contained" isClicked={isSignUpClicked}>
                  <BoldGreenText>Sign up</BoldGreenText>
                </SignUpButton>
              </>
            ) : (
              <>
                <SignUpButton onClick={toggleSignup} variant="contained" isClicked={isSignUpClicked}>
                  <BoldGreenText>Sign up</BoldGreenText>
                </SignUpButton>
                <LoginButton variant="contained" onClick={toggleLogin} isClicked={isLoginClicked}>
                  <BoldGreenText>Log in</BoldGreenText>
                </LoginButton>
              </>
            )}
          </div>
          {isLoginClicked ? (
            <>
<GreenLabel>{isUsernameFocused ? '' : 'Enter Username'}</GreenLabel>
<TextField
  variant="standard"
  placeholder={isUsername ? '' : 'User Name'}
  onFocus={() => setIsUsernameFocused(true)}
  onBlur={() => setIsUsernameFocused(false)}
  InputLabelProps={{
    style: { display: 'none' }, // Hide label when TextField is focused
  }}
  InputProps={{
    style: { color: 'green', background: 'linear-gradient(90deg, white 50%, black 50%)' },
  }}
  sx={{ marginBottom: '10px' }}
/>
              <GreenLabel>Enter Password</GreenLabel>
              <TextField
                variant="standard"
                placeholder="Enter Password"
                InputLabelProps={{
                  style: { color: 'green', fontWeight: 'bold' },
                }}
                InputProps={{
                  style: { color: 'green', background: 'linear-gradient(90deg, white 50%, black 50%)' },
                }}
                sx={{ marginBottom: '10px' }}
              />
              <Button
                variant="contained"
                style={{
                  width: '60%',
                  background: 'linear-gradient(to right, white 50%, black 50%)',
                  color: 'green',
                  marginBottom: '10px',
                }}
              >
                <BoldGreenText>Login</BoldGreenText>
              </Button>
            </>
          ) : (
            <>
              <GreenLabel>Name</GreenLabel>
              <TextField
                variant="standard"
                placeholder="Enter Name"
                InputLabelProps={{
                  style: { color: 'green', fontWeight: 'bold' },
                }}
                InputProps={{
                  style: { color: 'green', background: 'linear-gradient(90deg, white 50%, black 50%)' },
                }}
                sx={{ marginBottom: '10px' }}
              />
              <GreenLabel>Enter Username</GreenLabel>
              <TextField
                variant="standard"
                placeholder="Enter Username"
                InputLabelProps={{
                  style: { color: 'green', fontWeight: 'bold' },
                }}
                InputProps={{
                  style: { color: 'green', background: 'linear-gradient(90deg, white 50%, black 50%)' },
                }}
                sx={{ marginBottom: '10px' }}
              />
              <GreenLabel>Enter Password</GreenLabel>
              <TextField
                variant="standard"
                placeholder="Enter Password"
                InputLabelProps={{
                  style: { color: 'green', fontWeight: 'bold' },
                }}
                InputProps={{
                  style: { color: 'green', background: 'linear-gradient(90deg, white 50%, black 50%)' },
                }}
                sx={{ marginBottom: '10px' }}
              />
              <GreenLabel>Gender</GreenLabel>
              <TextField
                variant="standard"
                placeholder="Gender"
                InputLabelProps={{
                  style: { color: 'green', fontWeight: 'bold' },
                }}
                InputProps={{
                  style: { color: 'green', background: 'linear-gradient(90deg, white 50%, black 50%)' },
                }}
                sx={{ marginBottom: '10px' }}
              />
              <GreenLabel>Age</GreenLabel>
              <TextField
                variant="standard"
                placeholder="Age"
                InputLabelProps={{
                  style: { color: 'green', fontWeight: 'bold' },
                }}
                InputProps={{
                  style: { color: 'green', background: 'linear-gradient(90deg, white 50%, black 50%)' },
                }}
                sx={{ marginBottom: '10px' }}
              />
              <GreenLabel>Mobile</GreenLabel>
              <TextField
                variant="standard"
                placeholder="Mobile"
                InputLabelProps={{
                  style: { color: 'green', fontWeight: 'bold' },
                }}
                InputProps={{
                  style: { color: 'green', background: 'linear-gradient(90deg, white 50%, black 50%)' },
                }}
                sx={{ marginBottom: '10px' }}
              />
              <Button
                variant="contained"
                style={{
                  width: '60%',
                  background: 'linear-gradient(to right, white 50%, black 50%)',
                  color: 'green',
                  marginBottom: '10px',
                }}
              >
                <BoldGreenText>Sign up</BoldGreenText>
              </Button>
            </>
          )}
        </Wrapper>
      </Box>
    </Component>
  );
};

export default Login;
