import React, { useState } from "react";
import { Box, FormControl, styled, InputBase, Button, TextareaAutosize } from "@mui/material";

const Container = styled(Box)`
  margin: 50px 100px;
`;

const StyledControl = styled(FormControl)`
  margin-top: 10px;
  display: flex;
  flex-direction: row;
`;

const InputTextField = styled(InputBase)`
  flex: 1;
  margin: 0 30px;
  font-size: 25px;
`;

const MaxLengthInputTextField = styled(InputTextField)`
  max-width: 300px; // Limiting width to control the maximum characters visually
`;

const Textarea = styled(TextareaAutosize)`
  width: 100%;
  margin-top: 50px;
  font-size: 18px;
  border: none;
  background-color: black;
  color: white; /* Set text color to white */
  padding: 10px;
  &:focus-visible {
    outline: none;
  }
`;
const PublishButton = styled(Button)`
  width: auto; /* Adjusted width */
  height: auto; /* Adjusted height */
  padding: 8px 16px; /* Adjusted padding */
  display: flex;
  justify-content: flex-end;
`;


const initialPost = {
  title: '',
  description: '',
  username: '',
  gender: '',
  age: '',
  country: '',
  state: '',
  createdDate: new Date()
}

const CreatePost = () => {
  const [post, setPost] = useState(initialPost);

  // Handler for description change
  const handleDescriptionChange = (e) => {
    let newValue = e.target.value;

    newValue = newValue.replace(/\bsex\b/gi, 's*x');
    newValue = newValue.replace(/\bbetichod\b/gi, 'beti***d');

    newValue = newValue.replace(/\d/g, '');

    // Update the state
    setPost({ ...post, description: newValue });
  };

  return (
    <Container>
      <StyledControl>
      <InputTextField
          placeholder="Name"
          value={post.username}
          onChange={(e) => setPost({ ...post, username: e.target.value })}
        />
        <InputTextField
          placeholder="Gender"
          value={post.gender}
          onChange={(e) => setPost({ ...post, gender: e.target.value })}
        />
        <InputTextField
          placeholder="Age"
          value={post.age}
          onChange={(e) => setPost({ ...post, age: e.target.value })}
        />
        <InputTextField
          placeholder="Country"
          value={post.country}
          onChange={(e) => setPost({ ...post, country: e.target.value })}
        />
        <InputTextField
          placeholder="State"
          value={post.state}
          onChange={(e) => setPost({ ...post, state: e.target.value })}
        />
        <MaxLengthInputTextField
          placeholder="Title"
          value={post.title}
          onChange={(e) => setPost({...post, title: e.target.value})}
          inputProps={{ maxLength: 300 }}
       />
          <PublishButton variant="contained">
            Publish
          </PublishButton>

      </StyledControl>
      <Textarea
        minRows={5}
        placeholder="Tell your story..."
        value={post.description}
        onChange={handleDescriptionChange}
      />
    </Container>
  );
};

export default CreatePost;
