import { useState } from 'react'; 
import { Box, TextField, Button, styled } from '@mui/material'; 
import { useMediaQuery } from '@mui/material';

// Your styled components...

const Login = () => {
  const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png';

  const [isSignUpClicked, setSignUpClicked] = useState(false);
  const [isLoginClicked, setLoginClicked] = useState(true);
  const [isUsernameFocused, setIsUsernameFocused] = useState(false);
  const [signup, setSignup] = useState(signupInitialValues);

  const handleFocus = () => {
    setIsUsernameFocused(true);
  };

  const handleBlur = () => {
    setIsUsernameFocused(false);
  };

  const toggleSignup = () => {
    setSignUpClicked(true);
    setLoginClicked(false);
  };

  const toggleLogin = () => {
    setSignUpClicked(false);
    setLoginClicked(true);
  };

  const onInputChange = (e) => {
    setSignup({ ...signup, [e.target.name]: e.target.value });
  };

  const isSmallScreen = useMediaQuery((theme) => theme.breakpoints.down('sm'));

  return (
    <Component>
      <Box>
        <Image src={imageURL} alt="login" />
        <Wrapper>
          <div>
            {isLoginClicked ? (
              <>
                <LoginButton variant="contained" onClick={toggleLogin} isClicked={isLoginClicked}>
                  <BoldBlackText>Log in</BoldBlackText>
                </LoginButton>
                <SignUpButton onClick={toggleSignup} variant="contained" isClicked={isSignUpClicked}>
                  <BoldWhiteText>Sign up</BoldWhiteText>
                </SignUpButton>
              </>
            ) : (
              <>
                <SignUpButton onClick={toggleSignup} variant="contained" isClicked={isSignUpClicked}>
                  <BoldBlackText>Sign up</BoldBlackText>
                </SignUpButton>
                <LoginButton variant="contained" onClick={toggleLogin} isClicked={isLoginClicked}>
                  <BoldWhiteText>Log in</BoldWhiteText>
                </LoginButton>
              </>
            )}
          </div>
          {isSmallScreen ? (
            <MobileViewComponents
              handleFocus={handleFocus}
              handleBlur={handleBlur}
              isUsernameFocused={isUsernameFocused}
              onInputChange={onInputChange}
              signup={signup}
            />
          ) : (
            <DesktopViewComponents
              handleFocus={handleFocus}
              handleBlur={handleBlur}
              isUsernameFocused={isUsernameFocused}
              onInputChange={onInputChange}
              signup={signup}
            />
          )}
        </Wrapper>
      </Box>
    </Component>
  );
};

const MobileViewComponents = ({
  handleFocus,
  handleBlur,
  isUsernameFocused,
  onInputChange,
  signup,
}) => (
  <>
    {/* Render mobile view components here */}
  </>
);

const DesktopViewComponents = ({
  handleFocus,
  handleBlur,
  isUsernameFocused,
  onInputChange,
  signup,
}) => (
  <>
    {/* Render desktop view components here */}
  </>
);

export default Login;
