import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, Box, Avatar, IconButton, Tooltip } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import lines from '../../constants/lines'; 

const Header = () => {
  const [currentLine, setCurrentLine] = useState(0);
  const [avatarText, setAvatarText] = useState("LIFE IS");

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentLine(prevLine => (prevLine + 1) % lines.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const handleEditAvatar = () => {
    const newText = prompt('Enter new text for avatar:');
    if (newText !== null) {
      setAvatarText(newText);
    }
  };

  const maxLength = Math.max(...lines.map(line => line.length)); 

  return (
    <div style={{ overflowX: 'hidden' }}> 
      <AppBar position="static" sx={{ backgroundColor: 'black' }}>
        <Toolbar sx={{ display: 'flex', justifyContent: 'flex-end', width: '100%' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', marginRight: '10px' }}>
            <Typography variant="h6" component="div" sx={{ color: 'white' }}>
              {avatarText}
            </Typography>
            <Avatar sx={{ width: 60, height: 60, marginLeft: 10, backgroundColor: 'white' }}>
              {avatarText.charAt(0)}
            </Avatar>
            <Tooltip title="Edit Avatar">
              <IconButton onClick={handleEditAvatar}>
                <EditIcon />
              </IconButton>
            </Tooltip>
          </Box>
          <Typography variant="body1" sx={{ wordWrap: 'break-word', backgroundColor: 'white', padding: '20px', marginTop: '20px', marginBottom: '20px', marginLeft: '10px', marginRight: `${maxLength * 8}px`, color: 'white', boxShadow: '0px 0px 5px 2px rgba(0,0,0,0.2)' }}>
            {lines[currentLine]}
          </Typography>
        </Toolbar>
      </AppBar>
    </div>
  );
};

export default Header;
