import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, Box, Button } from '@mui/material';
import lines from './lines'; // Importing lines from the external file

const Header = () => {
  const [currentLine, setCurrentLine] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentLine((prevLine) => (prevLine + 1) % lines.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const maxLength = Math.max(...lines.map(line => line.length)); // Find the length of the longest line

  const appBarColor = 'black'; // Set the AppBar background color here

  const appBarMargin = {
    marginLeft: appBarColor === 'white' ? '290px' : '0px', // Adjust margin based on AppBar color
    marginRight: appBarColor === 'white' ? `${maxLength * 8}px` : 'auto', // Adjust margin based on AppBar color
  };

  return (
    <AppBar position="static" sx={{ backgroundColor: appBarColor }}>
      <Toolbar sx={{ display: 'flex', justifyContent: 'center', width: '100%' }}>
        <Box sx={{ display: 'flex', alignItems: 'center', ...appBarMargin }}>
          <Typography variant="h6" component="div">
            LIFE IS
          </Typography>
          <Typography variant="body1" sx={{ wordWrap: 'break-word', backgroundColor: 'white', padding: '20px', marginTop: '20px', marginBottom: '20px', marginLeft: '10px', marginRight: `${maxLength * 8}px`, color: 'black', boxShadow: '0px 0px 5px 2px rgba(0,0,0,0.2)' }}>
            {lines[currentLine]}
          </Typography>
        </Box>
        <Button variant="contained" style={{ color: appBarColor === 'white' ? 'black' : 'white', backgroundColor: appBarColor === 'white' ? 'white' : 'black', marginBottom: '20px', marginLeft: '30px', marginRight: '20px', boxShadow: '0px 0px 5px 2px rgba(0,0,0,0.2)' }}>Login</Button>
        <Button variant="contained" style={{ color: appBarColor === 'white' ? 'white' : 'black', backgroundColor: appBarColor === 'white' ? 'black' : 'white', marginBottom: '20px',  marginRight: '80px', border: '1px solid white', boxShadow: '0px 0px 5px 2px rgba(0,0,0,0.2)' }}>Signup</Button>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
