import { useState } from 'react'; 
import { Box, TextField, Button, styled } from '@mui/material'; 
import { useMediaQuery } from '@mui/material';

const Component = styled(Box)` 
  width: 500px; 
  margin: auto; 
  box-shadow: 5px 2px 5px 2px rgb(0 0 0 / 0.6); 
  background: linear-gradient(to right, black 50%, white 50%); 
`;

const Image = styled('img')` 
  width: 100px; 
  display: flex; 
  margin: auto; 
  padding: 50px 0 0;
`;

const Wrapper = styled(Box)` 
  padding: 30px 30px; 
  display: flex; 
  flex-direction: column; 
  align-items: center; 
  & > * {
    margin-bottom: 10px; 
  }
`;

const StyledTextField = styled(TextField)` 
  color: ${({ focused }) => focused ? 'green' : 'inherit'}; 
  background: ${({ focused }) => focused ? 'linear-gradient(90deg, white 50%, black 50%)' : 'inherit'}; 
`;

const SignUpButton = styled(Button)(({ theme, isClicked }) => ({
  background: isClicked ? 'white' : 'black',
  color: isClicked ? 'white' : 'black',
  transition: 'background-color 0.3s, color 0.3s',
  boxShadow: isClicked ? '0px 0px 10px 9px rgba(255, 255, 255, 0.9)' : 'none',
  height: '40px',
  width: '100px',
  margin: '19px 30px',
  '&:hover': {
    backgroundColor: isClicked ? 'black' : 'black',
    boxShadow: isClicked ? '0px 0px 10px 9px rgba(255, 255, 255, 0.9)' : '0px 0px 10px 9px rgba(0, 0, 0, 0.9)',
  },
}));

const LoginButton = styled(Button)(({ theme, isClicked }) => ({
  background: isClicked ? 'white' : 'black',
  color: isClicked ? 'black' : 'white',
  boxShadow: isClicked ? '0px 0px 10px 9px rgba(255, 255, 255, 0.9)' : 'none',
  transition: 'background-color 0.3s, color 0.3s',
  height: '40px',
  width: '100px',
  margin: '29px 35px',
  '&:hover': {
    backgroundColor: 'black',
    boxShadow: isClicked ? '0px 0px 10px 9px rgba(255, 255, 255, 0.9)' : '0px 0px 10px 9px rgba(0, 0, 0, 0.9)',
  },
}));

const Label = styled('span')` 
  color: black; 
  font-weight: bold; 
  text-align: center; 
`;

const BoldBlackText = styled('span')` 
  color: black; 
  font-weight: bold; 
  text-align: center; 
`;

const BoldWhiteText = styled('span')` 
  color: white; 
  font-weight: bold; 
  text-align: center; 
`;

const signupInitialValues ={
  name: '',
  username: '',
  password: '',
  gender: '',
  age: '',
  mobile: ''
}

const Login = () => {
  const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png';

  const [isSignUpClicked, setSignUpClicked] = useState(false);
  const [isLoginClicked, setLoginClicked] = useState(true);
  const [isUsernameFocused, setIsUsernameFocused] = useState(false);
  const [signup, setSignup] = useState(signupInitialValues);
  
  // Add the useMediaQuery hook here
  const isSmallScreen = useMediaQuery('(max-width:600px)');
  
  const handleFocus = () => {
    setIsUsernameFocused(true);
  };

  const handleBlur = () => {
    setIsUsernameFocused(false);
  };

  const toggleSignup = () => {
    setSignUpClicked(true);
    setLoginClicked(false);
  };

  const toggleLogin = () => {
    setSignUpClicked(false);
    setLoginClicked(true);
  };

  const onInputChange = (e) => {
    setSignup({ ...signup, [e.target.name]: e.target.value });
  };

  return (
    <Component>
      <Box>
        <Image src={imageURL} alt="login" />
        <Wrapper>
          <div>
            {isLoginClicked ? (
              <>
                <LoginButton variant="contained" onClick={toggleLogin} isClicked={isLoginClicked}>
                  <BoldBlackText>Log in</BoldBlackText>
                </LoginButton>
                <SignUpButton onClick={toggleSignup} variant="contained" isClicked={isSignUpClicked}>
                  <BoldWhiteText>Sign up</BoldWhiteText>
                </SignUpButton>
              </>
            ) : (
              <>
                <SignUpButton onClick={toggleSignup} variant="contained" isClicked={isSignUpClicked}>
                  <BoldBlackText>Sign up</BoldBlackText>
                </SignUpButton>
                <LoginButton variant="contained" onClick={toggleLogin} isClicked={isLoginClicked}>
                  <BoldWhiteText>Log in</BoldWhiteText>
                </LoginButton>
              </>
            )}
          </div>
          {isLoginClicked ? (
            <>
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <Label style={{ backgroundColor: 'white', height: '30px', width: '120px', display: 'inline-block', textAlign: 'center' }}> UserName</Label>
                <StyledTextField
                  variant="standard"
                  placeholder="User Name"
                  InputLabelProps={{
                    style: { display: 'none' },
                  }}
                  InputProps={{
                    style: { color: isUsernameFocused ? 'white' : 'inherit', background: 'black' },
                    placeholderTextColor: 'white',
                  }}
                  sx={{ marginBottom: '10px',  marginLeft: isSmallScreen ? '10px' : '110px' }}
                  onFocus={handleFocus}
                  onBlur={handleBlur}
                />
              </div>
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <Label style={{ backgroundColor: 'white', height: '30px', width: '120px', display: 'inline-block', textAlign: 'center' }}>Password</Label>
                <StyledTextField
                  variant="standard"
                  placeholder="User Password"
                  InputLabelProps={{
                    style: { display: 'none' },
                  }}
                  InputProps={{
                    style: { color: isUsernameFocused ? 'white' : 'inherit', background: 'black' },
                    placeholderTextColor: 'white',
                  }}
                  sx={{ marginBottom: '10px', marginLeft: isSmallScreen ? '10px' : '107px' }}
                  onFocus={handleFocus}
                  onBlur={handleBlur}
                />
              </div>
            </>
          ) : (
            <>
              <TextField
                variant="standard"
                placeholder="User Name"
                InputLabelProps={{
                  style: { display: 'none' },
                }}
                InputProps={{
                  style: { color: isUsernameFocused ? 'white' : 'inherit', background: 'black' },
                  placeholderTextColor: 'white',
                }}
                sx={{ marginBottom: '10px' }}
                onFocus={handleFocus}
                onBlur={handleBlur}
              />
              <TextField
                variant="standard"
                placeholder="User Password"
                InputLabelProps={{
                  style: { display: 'none' },
                }}
                InputProps={{
                  style: { color: isUsernameFocused ? 'white' : 'inherit', background: 'black' },
                  placeholderTextColor: 'white',
                }}
                sx={{ marginBottom: '10px' }}
                onFocus={handleFocus}
                onBlur={handleBlur}
              />
              <TextField
                variant="standard"
                placeholder="Full Name"
                InputLabelProps={{
                  style: { display: 'none' },
                }}
                InputProps={{
                  style: { color: isUsernameFocused ? 'white' : 'inherit', background: 'black' },
                  placeholderTextColor: 'white',
                }}
                sx={{ marginBottom: '10px' }}
                onFocus={handleFocus}
                onBlur={handleBlur}
              />
              <TextField
                variant="standard"
                placeholder="Age"
                InputLabelProps={{
                  style: { display: 'none' },
                }}
                InputProps={{
                  style: { color: isUsernameFocused ? 'white' : 'inherit', background: 'black' },
                  placeholderTextColor: 'white',
                }}
                sx={{ marginBottom: '10px' }}
                onFocus={handleFocus}
                onBlur={handleBlur}
              />
              <TextField
                variant="standard"
                placeholder="Gender"
                InputLabelProps={{
                  style: { display: 'none' },
                }}
                InputProps={{
                  style: { color: isUsernameFocused ? 'white' : 'inherit', background: 'black' },
                  placeholderTextColor: 'white',
                }}
                sx={{ marginBottom: '10px' }}
                onFocus={handleFocus}
                onBlur={handleBlur}
              />
              <TextField
                variant="standard"
                placeholder="Mobile"
                InputLabelProps={{
                  style: { display: 'none' },
                }}
                InputProps={{
                  style: { color: isUsernameFocused ? 'white' : 'inherit', background: 'black' },
                  placeholderTextColor: 'white',
                }}
                sx={{ marginBottom: '10px' }}
                onFocus={handleFocus}
                onBlur={handleBlur}
              />
            </>
          )}
          {isLoginClicked ? (
            <Button variant="contained" style={{ background: 'white', color: 'black', height: '40px', width: '100px', margin: '29px 35px' }}>
              <BoldBlackText>Log in</BoldBlackText>
            </Button>
          ) : (
            <Button variant="contained" style={{ background: 'white', color: 'black', height: '40px', width: '100px', margin: '29px 35px' }}>
              <BoldBlackText>Sign up</BoldBlackText>
            </Button>
          )}
        </Wrapper>
      </Box>
    </Component>
  );
};

export default Login;
