import { useState } from 'react';
import { Box, TextField, Button, styled } from '@mui/material';

const Component = styled(Box)`
  width: 400px;
  margin: auto;
  box-shadow: 5px 2px 5px 2px rgb(0 0 0 / 0.6);
  background: linear-gradient(to right, black 50%, white 50%);
`;

const Image = styled('img')({
  width: 100,
  display: 'flex',
  margin: 'auto',
  padding: '50px 0 0',
});

const Wrapper = styled(Box)`
  padding: 20px 35px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const LoginButton = styled(Button)(({ theme }) => ({
  background: 'white',
  borderRadius: '2px', 
  color: 'black',
  boxShadow: '0px 0px 10px 9px rgba(255, 255, 255, 0.9)',
  transition: 'background-color 0.3s, color 0.3s',
  height: '40px', 
  width: '100px',
  margin: '10px', // Adding space between buttons
  '&:hover': {
    backgroundColor: 'black',
    color: 'white',
  },
}));

const SignUpButton = styled(Button)(({ theme }) => ({
  background: 'black',
  color: 'white',
  transition: 'background-color 0.3s, color 0.3s',
  boxShadow: '0px 0px 10px 9px rgba(0, 0, 0, 0.9)', 
  height: '40px', 
  width: '100px',
  margin: '10px', // Adding space between buttons
  '&:hover': {
    backgroundColor: 'white',
    color: 'black',
  },
}));

const CustomTextField = styled(TextField)(({ theme, isLogin }) => ({
  background: isLogin ? 'white' : 'black',
  '& input': {
    color: isLogin ? 'black' : 'white',
  },
}));

const CustomBottomButton = styled(Button)(({ theme, isLogin }) => ({
  background: isLogin ? 'white' : 'black',
  color: isLogin ? 'black' : 'white',
}));

const Login = () => {
  const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png';

  const [account, toggleAccount] = useState('login');

  const toggleSignup = () => {
    toggleAccount(account === 'login' ? 'signup' : 'login');
  };

  return (
    <Component>
      <Box>
        <Image src={imageURL} alt="login" />
        <Wrapper>
          <div>
            {account === 'login' ? (
              <>
                <LoginButton variant="contained">Login</LoginButton>
                <SignUpButton onClick={() => toggleSignup()} variant="contained">Sign in</SignUpButton>
              </>
            ) : (
              <>
                <SignUpButton onClick={() => toggleSignup()} variant="contained">Sign in</SignUpButton>
                <LoginButton variant="contained">Login</LoginButton>
              </>
            )}
          </div>
          {account === 'login' ? (
            <>
              <CustomTextField variant="standard" label="Enter Username" isLogin={true} />
              <CustomTextField variant="standard" label="Enter Password" isLogin={true} />
              <CustomBottomButton variant="contained" isLogin={true}>Login</CustomBottomButton>
            </>
          ) : (
            <>
              <CustomTextField variant="standard" label="Enter Name" />
              <CustomTextField variant="standard" label="Enter Username" />
              <CustomTextField variant="standard" label="Enter Password" />
              <CustomBottomButton variant="contained">Sign up</CustomBottomButton>
            </>
          )}
        </Wrapper>
      </Box>
    </Component>
  );
};

export default Login;
