import { BrowserRouter, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/account/Login';
import Home from './components/home/Home';
import Header from './components/headers/Header';
import CreatePost from './components/create/CreatePost'; // Ensure correct import path
import LoginHeader from './components/loginheader/LoginHeader';

// State management (replace with your preferred library if needed)
import { useState, createContext, useContext } from 'react';

const AuthContext = createContext(false); // Default isLoggedIn to false

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track login status

  // Handle login logic (replace with your actual authentication mechanism)
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  return (
    <BrowserRouter>
      <AuthContext.Provider value={{ isLoggedIn, setIsLoggedIn }}>
        {/* Conditionally render headers based on login status */}
        {isLoggedIn ? <Header /> : <LoginHeader />}

        <div style={{ marginTop: 8 }}>
          <Routes>
            <Route
              path="/login"
              element={<Login onLogin={handleLogin} />} // Pass login function to Login component
            />
            <Route path="/" element={<Home />} />
            <Route path="/create" element={<CreatePost />} /> {/* Unconditional rendering of CreatePost */}
            <Route path="/loginheader" element={<Navigate to="/" replace />} /> {/* Redirect from loginheader to home */}
          </Routes>
        </div>
      </AuthContext.Provider>
    </BrowserRouter>
  );
}

export default App;
