import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, Box } from '@mui/material';

const Header = () => {
  const [currentLine, setCurrentLine] = useState(0);
  const lines = [
    "Love and Loss",
    "Life is a magnificent journey filled with unpredictable twists and turns",
    "where each moment holds the potential for growth",
    "where each moment holds the potential for growth",
    "where each moment holds the potential for growth",
    "where each moment holds the potential for growth",
    "love, laughter, and learning."
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentLine((prevLine) => (prevLine + 1) % lines.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <AppBar position="static" sx={{ backgroundColor: 'black' }}>
      <Toolbar sx={{ display: 'flex', justifyContent: 'space-between', width: '100%' }}>
        <Typography variant="h6" component="div" sx={{ color: 'white' }}>
          Life is _____________
        </Typography>
        <Box sx={{ flex: 1, backgroundColor: 'white', padding: '10px', color: 'black' }}>
          <div>
            <Typography variant="body1" sx={{ wordWrap: 'break-word' }}>
              {lines[currentLine]}
            </Typography>
          </div>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
