import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, Box, Button } from '@mui/material';

const Header = () => {
  const [currentLine, setCurrentLine] = useState(0);
  const lines = [
    "Love and Loss",
    "Life is a magnificent journey filled with unpredictable twists and turns",
    // Add your other lines here
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentLine((prevLine) => (prevLine + 1) % lines.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const maxLength = Math.max(...lines.map(line => line.length)); // Find the length of the longest line

  return (
    <AppBar position="static" sx={{ backgroundColor: 'black' }}>
      <Toolbar sx={{ display: 'flex', justifyContent: 'center', width: '100%' }}>
        <Box sx={{ display: 'flex', alignItems: 'center', marginRight: 'auto' }}>
          <Typography variant="h6" component="div">
            LIFE IS
          </Typography>
          <Typography variant="body1" sx={{ wordWrap: 'break-word', backgroundColor: 'white', padding: '20px', marginTop: '20px', marginBottom: '20px', marginLeft: '10px', marginRight: `${maxLength * 8}px`, color: 'black' }}>
            {lines[currentLine]}
          </Typography>
        </Box>
        <Button variant="contained" style={{ color: 'white', backgroundColor: 'green', marginBottom: '20px', marginLeft: '10px' }}>Login</Button>
        <Button variant="contained" style={{ color: 'white', backgroundColor: 'green', marginBottom: '20px', marginLeft: '10px' }}>Signup</Button>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
