import React, { useState } from "react";
import { Box, FormControl, styled, InputBase, Button, TextareaAutosize } from "@mui/material";

const Container = styled(Box)`
  margin: 50px 100px;
`;

const StyledControl = styled(FormControl)`
  margin-top: 10px;
  display: flex;
  flex-direction: column;
`;

const InputTextField = styled(InputBase)`
  margin-bottom: 10px;
`;

const initialPost = {
  title: '',
  description: '',
  username: '',
  gender: '',
  age: '',
  country: '',
  state: '',
  startDate: '',
  endDate: '',
  duration: '',
};

const CreatePost = () => {
  const [post, setPost] = useState(initialPost);

  const handleStartDateChange = (e) => {
    setPost({ ...post, startDate: e.target.value });
  };

  const handleEndDateChange = (e) => {
    setPost({ ...post, endDate: e.target.value });
    calculateDuration(post.startDate, e.target.value);
  };

  const calculateDuration = (startDate, endDate) => {
    const start = new Date(startDate);
    const end = new Date(endDate);
    const diffTime = Math.abs(end - start);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)); 
    setPost({ ...post, duration: `${diffDays} days` });
  };

  return (
    <Container>
      <StyledControl>
        <Box display="flex" flexDirection="column">
          <InputTextField
            placeholder="Name"
            value={post.username}
            onChange={(e) => setPost({ ...post, username: e.target.value })}
          />
          <InputTextField
            placeholder="Gender"
            value={post.gender}
            onChange={(e) => setPost({ ...post, gender: e.target.value })}
          />
          <InputTextField
            placeholder="Age"
            value={post.age}
            onChange={(e) => setPost({ ...post, age: e.target.value })}
          />
          <InputTextField
            placeholder="Country"
            value={post.country}
            onChange={(e) => setPost({ ...post, country: e.target.value })}
          />
          <InputTextField
            placeholder="State"
            value={post.state}
            onChange={(e) => setPost({ ...post, state: e.target.value })}
          />
          <InputTextField
            type="date"
            placeholder="Start Date"
            value={post.startDate}
            onChange={handleStartDateChange}
          />
          <InputTextField
            type="date"
            placeholder="End Date"
            value={post.endDate}
            onChange={handleEndDateChange}
          />
        </Box>
        <Button variant="contained" onClick={handlePublish}>
          Publish
        </Button>
      </StyledControl>
      <Textarea
        minRows={5}
        placeholder="Tell your story..."
        value={post.description}
        onChange={handleDescriptionChange}
      />
      <p>{post.duration}</p>
    </Container>
  );
};

export default CreatePost;
