import { useState } from 'react';
import { Box, TextField, Button, styled } from '@mui/material';

const Component = styled(Box)`
  width: 500px;
  margin: auto;
  box-shadow: 5px 2px 5px 2px rgb(0 0 0 / 0.6);
  background: linear-gradient(to right, black 50%, white 50%);
`;

const Image = styled('img')`
  width: 100px;
  display: flex;
  margin: auto;
  padding: 50px 0 0;
`;

const Wrapper = styled(Box)`
  padding: 30px 30px;
  display: flex;
  flex-direction: column;
  align-items: center;
  & > * {
    margin-bottom: 10px;
  }
`;

const StyledTextField = styled(TextField)`
  color: ${({ focused }) => focused ? 'green' : 'inherit'};
  background: ${({ focused }) => focused ? 'linear-gradient(90deg, white 50%, black 50%)' : 'inherit'};
`;

const SignUpButton = styled(Button)(({ theme, isClicked }) => ({
  background: isClicked ? 'black' : 'white',
  color: isClicked ? 'white' : 'black',
  transition: 'background-color 0.3s, color 0.3s',
  boxShadow: isClicked ? '0px 0px 10px 9px rgba(0, 0, 0, 0.9)' : 'none',
  height: '40px',
  width: '100px',
  margin: '19px 30px',
  '&:hover': {
    backgroundColor: isClicked ? 'black' : 'white',
    color: isClicked ? 'white' : 'black',
    boxShadow: isClicked ? '0px 0px 10px 9px rgba(0, 0, 0, 0.9)' : 'none',
  },
  [theme.breakpoints.down('sm')]: {},
}));

const LoginButton = styled(Button)(({ theme, isClicked }) => ({
  background: isClicked ? 'white' : 'black',
  color: isClicked ? 'black' : 'white',
  boxShadow: isClicked ? '0px 0px 10px 9px rgba(255, 255, 255, 0.9)' : '0px 0px 10px 9px rgba(0, 0, 0, 0.9)',
  transition: 'background-color 0.3s, color 0.3s',
  height: '40px',
  width: '100px',
  margin: '29px 35px',
  '&:hover': {
    backgroundColor: 'white',
    color: 'black',
  },
  [theme.breakpoints.down('sm')]: {},
}));

const BoldGreenText = styled('span')`
  color: green;
  font-weight: bold;
`;

const GreenLabel = styled('span')`
  color: green;
  font-weight: bold;
  margin-bottom: 5px;
  text-align: left;
  display: block;
  background-color: white; /* Added background color */
  width: 100%; /* Make the background cover the entire width */
`;
const Label = styled('span')`
  color: green;
  font-weight: bold;
  margin-bottom: 5px;
  text-align: left;
  display: block;
  background-color: white; /* Added background color */
  width: 100%; /* Make the background cover the entire width */
`;

const Login = () => {
  const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png';

  const [isSignUpClicked, setSignUpClicked] = useState(false);
  const [isLoginClicked, setLoginClicked] = useState(true);
  const [isUsernameFocused, setIsUsernameFocused] = useState(false);

  const handleFocus = () => {
    setIsUsernameFocused(true);
  };

  const handleBlur = () => {
    setIsUsernameFocused(false);
  };

  const toggleSignup = () => {
    setSignUpClicked(true);
    setLoginClicked(false);
  };

  const toggleLogin = () => {
    setSignUpClicked(false);
    setLoginClicked(true);
  };

  return (
    <Component>
      <Box>
        <Image src={imageURL} alt="login" />
        <Wrapper>
          <div>
            {isLoginClicked ? (
              <>
                <LoginButton variant="contained" onClick={toggleLogin} isClicked={isLoginClicked}>
                  <BoldGreenText>Log in</BoldGreenText>
                </LoginButton>
                <SignUpButton onClick={toggleSignup} variant="contained" isClicked={isSignUpClicked}>
                  <BoldGreenText>Sign up</BoldGreenText>
                </SignUpButton>
              </>
            ) : (
              <>
                <SignUpButton onClick={toggleSignup} variant="contained" isClicked={isSignUpClicked}>
                  <BoldGreenText>Sign up</BoldGreenText>
                </SignUpButton>
                <LoginButton variant="contained" onClick={toggleLogin} isClicked={isLoginClicked}>
                  <BoldGreenText>Log in</BoldGreenText>
                </LoginButton>
              </>
            )}
          </div>
          {isLoginClicked ? (
            <>
<div style={{ display: 'flex', alignItems: 'center' }}>
  <BlackLabel>Enter Username</BlackLabel>
  <StyledTextField
    variant="standard"
    placeholder="User Name"
    InputLabelProps={{
      style: { display: 'none' }, // Hide label when TextField is focused
    }}
    InputProps={{
      style: { color: isUsernameFocused ? 'white' : 'inherit', background: 'black' },
      placeholderTextColor: 'black', // Set placeholder text color
    }}
    sx={{ marginBottom: '10px', marginLeft: '10px' }}
    onFocus={handleFocus}
    onBlur={handleBlur}
  />
</div>

<div style={{ display: 'flex', alignItems: 'center' }}>
  <GreenLabel>Enter Password</GreenLabel>
  <StyledTextField
    variant="standard"
    placeholder="Enter Password"
    InputLabelProps={{
      style: { display: 'none' }, // Hide label when TextField is focused
    }}
    InputProps={{
      style: { color: isUsernameFocused ? 'green' : 'inherit', background: 'black' },
      placeholderTextColor: 'black', // Set placeholder text color
    }}
    sx={{ marginBottom: '10px', marginLeft: '10px' }}
    onFocus={handleFocus}
    onBlur={handleBlur}
  />
</div>

              <Button
                variant="contained"
                style={{
                  width: '60%',
                  background: 'linear-gradient(to right, white 50%, black 50%)',
                  color: 'green',
                  marginBottom: '10px',
                }}
