import React, { useState } from "react";
import { Box, FormControl, styled, InputBase, Button, TextareaAutosize, Select, MenuItem } from "@mui/material";
import { DatePicker } from "@mui/lab";

const Container = styled(Box)`
  margin: 50px 100px;
`;

const StyledControl = styled(FormControl)`
  margin-top: 10px;
  display: flex;
  flex-direction: column;
  width: 100%;
  justify-content: space-between;
`;

const InputTextField = styled(InputBase)`
  margin: 10px 0;
  font-size: 16px;
`;

const SelectField = styled(Select)`
  margin: 10px 0;
  font-size: 16px;
`;

const Textarea = styled(TextareaAutosize)`
  width: 100%;
  margin-top: 20px;
  font-size: 18px;
  border: none;
  background-color: black;
  color: white;
  padding: 10px;
  &:focus-visible {
    outline: none;
  }
`;

const PublishButton = styled(Button)`
  align-self: flex-end;
  margin-top: 10px;
`;

const initialPost = {
  name: '',
  age: '',
  relationshipYears: '',
  relationshipMonths: '',
  description: '',
};

const CreatePost = () => {
  const [post, setPost] = useState(initialPost);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPost({ ...post, [name]: value });
  };

  const handleDateChange = (date) => {
    // Assuming date is a Date object
    const currentDate = new Date();
    const diff = Math.abs(currentDate - date); // Difference in milliseconds
    const years = Math.floor(diff / (1000 * 60 * 60 * 24 * 365));
    const months = Math.floor((diff % (1000 * 60 * 60 * 24 * 365)) / (1000 * 60 * 60 * 24 * 30));
    setPost({ ...post, age: years, relationshipYears: years, relationshipMonths: months });
  };

  const handleSubmit = () => {
    // Handle form submission here
    console.log(post);
  };

  return (
    <Container>
      <StyledControl>
        <InputTextField
          placeholder="Enter your name"
          name="name"
          value={post.name}
          onChange={handleInputChange}
        />
        <InputTextField
          placeholder="Enter your age"
          type="number"
          name="age"
          value={post.age}
          onChange={handleInputChange}
        />
        <div>
          <SelectField
            value={post.relationshipYears}
            onChange={handleInputChange}
            name="relationshipYears"
          >
            {Array.from({ length: 101 }, (_, i) => i).map((year) => (
              <MenuItem key={year} value={year}>{year}</MenuItem>
            ))}
          </SelectField>
          <span>Years in the relationship</span>
          <SelectField
            value={post.relationshipMonths}
            onChange={handleInputChange}
            name="relationshipMonths"
          >
            {Array.from({ length: 12 }, (_, i) => i).map((month) => (
              <MenuItem key={month} value={month}>{month}</MenuItem>
            ))}
          </SelectField>
          <span>Months</span>
        </div>
        <Textarea
          minRows={5}
          placeholder="Tell your story..."
          value={post.description}
          onChange={handleInputChange}
        />
        <PublishButton variant="contained" onClick={handleSubmit}>
          Publish
        </PublishButton>
      </StyledControl>
    </Container>
  );
};

export default CreatePost;
