import { Box, TextField, Button, styled } from '@mui/material';

const Component = styled(Box)`
  width: 400px;
  margin: auto;
  box-shadow: 5px 2px 5px 2px rgb(0 0 0 / 0.6);
  background: linear-gradient(to right, black 50%, white 50%);
`;

const Image = styled('img')({
  width: 100,
  display: 'flex',
  margin: 'auto',
  padding: '50px 0 0',
});

const Wrapper = styled(Box)`
  padding: 20px 35px;
  display: flex;
  flex-direction: column;
  align-items: center;

  & > div {
    display: flex;
    gap: 80px; /* Increase gap between buttons */
  }

  & > div > button {
    flex: 1; /* Make buttons take equal width */
  }
`;

const LoginButton = styled(Button)({
  background: 'white',
  'border-radius': '2px', 
  color: 'black',
  'box-shadow': '0px 0px 10px 9px rgba(255, 255, 255, 0.9)',
  transition: 'background-color 0.3s, color 0.3s',
  height: '40px', 
  width: '100px',
  '&:hover': {
    backgroundColor: 'black',
    color: 'white',
  },
});

const SignInButton = styled(Button)({
  background: 'black',
  color: 'white',
  transition: 'background-color 0.3s, color 0.3s',
  'box-shadow': '0px 0px 10px 9px rgba(0, 0, 0, 0.9)', 
  height: '40px', 
  width: '100px',
  '&:hover': {
    backgroundColor: 'white',
    color: 'black',
  },
});



const Login = () => {
  const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png';

  return (
    <Component>
      <Box>
        <Image src={imageURL} alt="login" />
        {/* <Wrapper>
          <div>
            <LoginButton variant="contained">Login</LoginButton>
            <SignInButton variant="contained">Sign in</SignInButton>
          </div>
          <TextField variant="standard" lable = "Enter Username" />
          <TextField variant="standard" lable ="Enter Password"/>
           <SignInButton variant="contained">Login in</SignInButton>
        </Wrapper> */}
        {/* <Wrapper>
          <div>
            <LoginButton variant="contained">Login</LoginButton>
            <SignInButton variant="contained">Sign in</SignInButton>
          </div>
          <TextField variant="standard" lable = "Enter Name" />
          <TextField variant="standard" lable = "Enter UserName" />
          <TextField variant="standard" lable ="Enter Password"/>
          <SignInButton variant="contained">Sign in</SignInButton>
        </Wrapper> */}
      </Box>
    </Component>
  );
}

export default Login;
