import React, { useState } from "react";
import { Box, FormControl, styled, InputBase, Button, TextareaAutosize } from "@mui/material";

const Container = styled(Box)`
  margin: 50px 100px;
  display: flex;
  flex-direction: column;
  align-items: center; /* Center aligning for the form */
`;

const StyledControl = styled(FormControl)`
  margin-top: 10px;
  display: flex;
  flex-direction: column; /* Changed to column */
  width: 100%; /* Ensuring the form takes full width */
  justify-content: space-between; /* Pushing items to opposite ends */
`;

const InputTextField = styled(InputBase)`
  flex: 1;
  margin: 5px 0; /* Adjusted margin */
  font-size: 16px;
`;

const MaxLengthInputTextField = styled(InputTextField)`
  max-width: 300px; // Limiting width to control the maximum characters visually
`;

const Textarea = styled(TextareaAutosize)`
  width: 100%;
  margin-top: 20px;
  font-size: 18px;
  border: none;
  background-color: black; /* Set background color to black */
  color: white; /* Set text color to white */
  padding: 10px; /* Add padding for better visibility */
  &:focus-visible {
    outline: none;
  }
`;

const initialPost = {
  title: '',
  description: '',
  username: '',
  gender: '',
  age: '',
  country: '',
  state: '',
  createdDate: new Date()
}

const CreatePost = () => {
  const [post, setPost] = useState(initialPost);

  // Handler for title change
  const handleTitleChange = (e) => {
    let newValue = e.target.value;
    newValue = newValue.replace(/\bsex\b/gi, 's*x');
    newValue = newValue.replace(/\bbetichod\b/gi, 'beti***d');
    newValue = newValue.replace(/\d/g, '');
    // Update the state
    setPost({ ...post, title: newValue });
  };

  // Handler for description change
  const handleDescriptionChange = (e) => {
    let newValue = e.target.value;

    // Apply text replacements
    newValue = newValue.replace(/\bsex\b/gi, 's*x');
    newValue = newValue.replace(/\bbetichod\b/gi, 'beti***d');
    newValue = newValue.replace(/\d/g, '');

    // Update the state
    setPost({ ...post, description: newValue });
  };

  return (
    <Container>
      <StyledControl>
        <InputTextField
          placeholder="Name"
          value={post.username}
          onChange={(e) => setPost({ ...post, username: e.target.value })}
        />
        <InputTextField
          placeholder="Gender"
          value={post.gender}
          onChange={(e) => setPost({ ...post, gender: e.target.value })}
        />
        <InputTextField
          placeholder="Age"
          value={post.age}
          onChange={(e) => setPost({ ...post, age: e.target.value })}
        />
        <InputTextField
          placeholder="Country"
          value={post.country}
          onChange={(e) => setPost({ ...post, country: e.target.value })}
        />
        <InputTextField
          placeholder="State"
          value={post.state}
          onChange={(e) => setPost({ ...post, state: e.target.value })}
        />
      </StyledControl>
      <StyledControl>
        <MaxLengthInputTextField
          placeholder="Title"
          value={post.title}
          onChange={handleTitleChange}
          inputProps={{ maxLength: 300 }}
        />
        <Button variant="contained">
          Publish
        </Button>
      </StyledControl>
      <Textarea
        minRows={5}
        placeholder="Tell your story..."
        value={post.description}
        onChange={handleDescriptionChange}
      />
    </Container>
  );
};

export default CreatePost;
