import { useState } from 'react';
import { Box, TextField, Button, styled } from '@mui/material';

const Component = styled(Box)`
  width: 400px;
  margin: auto;
  box-shadow: 5px 2px 5px 2px rgb(0 0 0 / 0.6);
  background: linear-gradient(to right, black 50%, white 50%);
`;

const Image = styled('img')({
  width: 100,
  display: 'flex',
  margin: 'auto',
  padding: '50px 0 0',
});

const Wrapper = styled(Box)`
  padding: 20px 35px ;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const LoginButton = styled(Button)({
  background: 'white',
  'border-radius': '2px', 
  color: 'black',
  'box-shadow': '0px 0px 10px 9px rgba(255, 255, 255, 0.9)',
  transition: 'background-color 0.3s, color 0.3s',
  height: '40px', 
  width: '100px',
  margin: '0 25px', // Add margin for spacing
  '&:hover': {
    backgroundColor: 'black',
    color: 'white',
  },
});

const SignUpButton = styled(Button)({
  background: 'black',
  color: 'white',
  transition: 'background-color 0.3s, color 0.3s',
  'box-shadow': '0px 0px 10px 9px rgba(0, 0, 0, 0.9)', 
  height: '40px', 
  width: '100px',
  margin: '0 25px', // Add margin for spacing
  '&:hover': {
    backgroundColor: 'white',
    color: 'black',
  },
});

const Login = () => {
    const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png';
  
    const [account, toggleAccount] = useState('login');
  
    const toggleSignup = () => {
      toggleAccount(account === 'login' ? 'signup' : 'login');
    };
  
    const toggleLogin = () => {
      if (account === 'signup') {
        toggleSignup(); // If in signup mode, switch to login mode
      }
    };
    
  
    return (
      <Component>
        <Box>
          <Image src={imageURL} alt="login" />
          <Wrapper>
            <div>
              {account === 'login' ? (
                <>
                  <LoginButton variant="contained" onClick={toggleLogin}>Log in</LoginButton>
                  <SignUpButton onClick={toggleSignup} variant="contained">Sign up</SignUpButton>
                </>
              ) : (
                <>
                  <SignUpButton onClick={toggleSignup} variant="contained">Signup</SignUpButton>
                  <LoginButton variant="contained" onClick={toggleLogin}>Log in</LoginButton>
                </>
              )}
            </div>
            {account === 'login' ? (
              <>
                <TextField variant="standard" label="Enter Username" />
                <TextField variant="standard" label="Enter Password" />
                <Button variant="contained">Log in</Button>
              </>
            ) : (
              <>
                <TextField variant="standard" label="Enter Name" />
                <TextField variant="standard" label="Enter Username" />
                <TextField variant="standard" label="Enter Password" />
                <Button variant="contained">Sign up</Button>
              </>
            )}
          </Wrapper>
        </Box>
      </Component>
    );
  };
  
  export default Login;
